mapper.select:
SELECT * FROM (
SELECT rev_no, rev_Name, rev_Id, rev_Title, rev_Cont, rev_Readcount,
rev_Date, rev_Rate
FROM review



) as T1 ORDER BY rev_date DESC
LIMIT #{skip},#{amount}

mapper.update:
UPDATE review SET rev_Readcount = rev_Readcount + 1
WHERE
rev_No=#{rev_No}

UPDATE review SET
rev_Title = #{rev_Title},
rev_Cont =
#{rev_Cont},
rev_Rate =#{rev_Rate}
WHERE rev_No=#{rev_No}
