mapper.select:
		SELECT * FROM (
			SELECT @ROWNUM := @ROWNUM + 1 as rn, q_No, q_Id, q_Title, q_Content, q_Date, q_Secret,
				q_Answer, q_Pno, q_Ptitle, q_Pid, q_Magdate, q_Magcontent  
    			from qnatofarmer where q_pno LIKE CONCAT('%',#{q_Content},'%')


							and q_answer = 1


							and q_answer = 0


				order by q_no desc
		) RESULT
		WHERE RN BETWEEN #{start} AND #{end}

